<style type="text/css">
#wrapper {
    opacity: 0;
    -webkit-transition: all .3s ease-out;
    transition: all .3s ease-out;
}

#loader-wrapper {
    position: fixed;
    top: 0;
    left: 0;
    width: 100vw;
    height: 100vh;
    z-index: 9999;
    background-color: #3C92A6;
    -webkit-transition: all .3s ease-out;
    transition: all .3s ease-out;
}

#loader {
    display: block;
    position: absolute;
    left: 50%;
    top: 50%;
    width: 33vw;
    z-index: 1001;
    max-width: 250px;
    -webkit-transform: translate(-50%, -50%);
    -ms-transform: translate(-50%, -50%);
    transform: translate(-50%, -50%);
    -webkit-transition: all .3s ease-out;
    transition: all .3s ease-out;
}

#loader::before {
    content: '';
    display: block;
    width: 100%;
    padding-bottom: 31.79%;
}

#loader>svg {
    display: block;
    width: 100%;
    position: absolute;
    height: auto;
    top: 0;
    left: 0;
}

.lds-ring {
    display: block;
    margin-top: 40px;
    margin-left: auto;
    margin-right: auto;
    position: relative;
    width: 64px;
    height: 64px;
}

.lds-ring div {
    -webkit-box-sizing: border-box;
    box-sizing: border-box;
    display: block;
    position: absolute;
    width: 51px;
    height: 51px;
    margin: 6px;
    border: 6px solid #fff;
    border-radius: 50%;
    -webkit-animation: lds-ring 1.2s cubic-bezier(.5, 0, .5, 1) infinite;
    animation: lds-ring 1.2s cubic-bezier(.5, 0, .5, 1) infinite;
    border-color: #fff transparent transparent transparent;
}

.lds-ring div:nth-child(1) {
    -webkit-animation-delay: -.45s;
    animation-delay: -.45s;
}

.lds-ring div:nth-child(2) {
    -webkit-animation-delay: -.3s;
    animation-delay: -.3s;
}

.lds-ring:nth-child(3) {
    -webkit-animation-delay: -.15s;
    animation-delay: -.15s;
}

.loaded #loader-wrapper {
    opacity: 0;
    visibility: hidden;
}

.loaded #wrapper {
    opacity: 1;
}

.no-js #loader-wrapper {
    display: none;
}

.no-js #wrapper {
    opacity: 1;
}

@-webkit-keyframes lds-ring {
    0% {
        -webkit-transform: rotate(0);
        transform: rotate(0);
    }

    100% {
        -webkit-transform: rotate(360deg);
        transform: rotate(360deg);
    }
}

@keyframes lds-ring {
    0% {
        -webkit-transform: rotate(0);
        transform: rotate(0);
    }

    100% {
        -webkit-transform: rotate(360deg);
        transform: rotate(360deg);
    }
}

